import React from "react";
import { StyleSheet, ScrollView } from "react-native";
import { Layout, Text, Button, Divider, useTheme } from "@ui-kitten/components";
import * as Yup from "yup";
import Form from "../../components/forms/Form";
import FormField from "../../components/forms/FormField";
import SubmitForm from "../../components/forms/SubmitForm";

const QualificationScreen = ({ navigation, route }) => {
  const theme = useTheme();
  const qualificationDetailsValidationSchema = Yup.object().shape({
    degree: Yup.string().trim().required("Required"),
    specialization: Yup.string().trim(),
    experienceYears: Yup.string(),
    bio: Yup.string().trim(),
  });

  return (
    <Layout style={styles.container}>
      <ScrollView style={{ width: "100%", paddingHorizontal: 10 }}>
        <>
          <Layout style={styles.category}>
            <Text category="h6">Qualification Details</Text>
            <Divider
              style={[
                styles.divider,
                { backgroundColor: theme["color-primary-500"] },
              ]}
            />
          </Layout>

          <Form
            initialValues={{
              degree: "",
              specialization: "",
              experienceYears: "",
              bio: "",
            }}
            validationSchema={qualificationDetailsValidationSchema}
            onSubmit={(values) => {
              console.log({ ...route.params, ...values });
            }}
          >
            <FormField label="Degree" placeholder="Degree" name="degree" />
            <FormField
              label="Specialization"
              placeholder="Specialization"
              name="specialization"
            />
            <FormField
              label="Years of Experience"
              placeholder="Years"
              keyboardType="number-pad"
              name="experienceYears"
            />
            <FormField
              label="Bio"
              placeholder="Write about yourself"
              multiline={true}
              name="bio"
              textStyle={{ minHeight: 100, textAlignVertical: "top" }}
            />
            <Layout
              style={{ flexDirection: "row", justifyContent: "space-evenly" }}
            >
              <SubmitForm
                label="Previous"
                btnStyle={{ width: "40%" }}
                onPress={() => navigation.goBack()}
              />
              <SubmitForm label="Submit" btnStyle={{ width: "40%" }} />
            </Layout>
          </Form>
        </>
      </ScrollView>
    </Layout>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: "center",
    justifyContent: "center",
    flex: 1,
  },
  heading: {
    alignSelf: "center",
  },
  category: {
    alignItems: "center",
    marginVertical: 30,
  },
  divider: {
    width: "20%",
    height: 2,
    borderRadius: 10,
    marginVertical: 10,
  },
  radioContainer: {
    marginLeft: 5,
    marginTop: 10,
    marginBottom: 2,
  },
  radioGroup: {
    flexDirection: "row",
  },
  btn: {
    marginTop: 15,
    width: "40%",
  },
});

export default QualificationScreen;
