import {
  REGISTER,
  LOGIN,
  FETCH_HOSPITAL_DATA,
  SET_USER,
  SET_USER_TYPE,
  SET_ISLOADING,
  SET_ERROR,
} from "../constants";

const initialState = {
  isLoading: false,
  userType: "",
  user: null,
  error: null,
};

export const authReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOGIN: {
      return state;
    }

    case REGISTER: {
      return state;
    }

    case SET_USER_TYPE: {
      return {
        ...state,
        userType: action.payload,
      };
    }

    case SET_USER: {
      return {
        ...state,
        isLoading: false,
        user: action.payload,
      };
    }

    case FETCH_HOSPITAL_DATA: {
      console.log(action.payload, "Hospital");
      return {
        ...state,
        isLoading: false,
        user: {
          ...state.user,
          hospital: action.payload,
        },
        error: null,
      };
    }

    case SET_ISLOADING: {
      return {
        ...state,
        isLoading: action.payload,
      };
    }

    case SET_ERROR: {
      return {
        ...state,
        error: action.payload,
      };
    }

    default: {
      return state;
    }
  }
};
