import React, { useCallback, memo, useRef, useState, useEffect } from "react";
import { FlatList, View, StyleSheet, Image } from "react-native";
import { create } from "apisauce";


export default function Carousel() {
  
  const [Articles, setArticles] = useState();

  useEffect(() => {
    const api = create({ baseURL: 'https://newsapi.org/v2' });

    async function fetchData() {
      await api
        .get(
          "/top-headlines?country=in&category=health&apiKey=c2c429428b644c709d33bc31f02cbb7f"
        )
        .then((Response) => {
          if (Response.ok) {
            console.log(JSON.parse(Response));
            setArticles(JSON.parse(Response));
          }
          else{
            console.log(Response.problem);
          }
        });
    }
    fetchData();

  })

  const _renderItem=({item,index})=>{
    return (
      <View style={{
          backgroundColor:'floralwhite',
          borderRadius: 5,
          height: 250,
          padding: 50,
          marginLeft: 25,
          marginRight: 25, }}>
        <Text style={{fontSize: 30}}>{item.title}</Text>
        <Text>{item.text}</Text>
      </View>

    )
}
  return (
    <>

        return (
            <View style={{ flex: 1, flexDirection:'row', justifyContent: 'center', }}>
                <Carousel
                  layout={"default"}
                  data={Articles}
                  sliderWidth={300}
                  itemWidth={300}
                  renderItem={_renderItem}
                  onSnapToItem = { index => setArticles({activeIndex:index}) } />
            </View>
        );
    </>
  );
}
